mapper.update:
update emp
username=#{username}, password=#{password}, name=#{name}, gender=#{gender}, phone=#{phone}, job=#{job}, salary=#{salary}, image=#{image}, entry_date=#{entryDate}, dept_id=#{deptId}, create_time=#{createTime}, update_time=#{updateTime}
where id=#{id}

mapper.select:
select e.* , d.name as deptName  from emp e left join dept d on e.dept_id=d.id


    e.name like concat("%",#{name},"%")


    and gender=#{gender}



        and entry_date between #{begin} and #{end}


order by e.update_time desc
