mapper.update:
update student set
    violation_count=#{violationCount},
    violation_score=#{violationScore},
    update_time=#{updateTime}
    where id=#{id}

mapper.select:
select s.id, s.name, s.no, s.gender, s.phone, s.id_card, s.is_college, s.address, s.degree, s.graduation_date, s.clazz_id, s.violation_count, s.violation_score, s.create_time, s.update_time,
       clazz.name as clazzName
from student s left join clazz on s.clazz_id=clazz.id


        s.name like concat("%",#{name},"%")


    and s.clazz_id=#{clazzId}


    and s.degree=#{degree}

select s.id, s.name, s.no, s.gender, s.phone, s.id_card, s.is_college, s.address, s.degree, s.graduation_date, s.clazz_id, s.violation_count, s.violation_score, s.create_time, s.update_time
,c.name clazzName from student s left join clazz c on s.clazz_id=c.id
where s.id=#{id}
